import React, { Component } from 'react'
import axios from 'axios';


export default class CreatePayment extends Component {

    constructor(props){
        super(props);
        this.state={

            fields: {},
            errors: {}
        }

        this.handleInputChange = this.handleInputChange.bind(this);
        this.submituserRegistrationForm = this.submituserRegistrationForm.bind(this);


 
    }

    handleInputChange = (e) =>{
        let fields = this.state.fields;
      fields[e.target.name] = e.target.value;
      this.setState({
        fields
      });

    }
  

    
  
  
  

    onSubmit = (e) =>{
        e.preventDefault();

        const {paymentType,invoiceNo,patientName,amount,payerName,nicNo,contactNo,emailAddress,address} = this.state;

        const data ={
            paymentType:paymentType,
            invoiceNo:invoiceNo,
            patientName:patientName,
            amount:amount,
            payerName:payerName,
            nicNo:nicNo,
            contactNo:contactNo,
            emailAddress:emailAddress,
            address:address
        }
        console.log(data)

        axios.post("/post/save",data).then((res) =>{
            if(res.data.success){
                this.setState(
                    {
                        paymentType:"",
                        invoiceNo:"",
                        patientName:"",
                        amount:"",
                        payerName:"",
                        nicNo:"",
                        contactNo:"",
                        emailAddress:"",
                        address:""
                    }

                    
                )
            }
        });
        
        this.props.history.push('/confirm');

        submituserRegistrationForm = (e) =>{
       
            if (this.validateForm()) {
                let fields = {};
                fields["patientName"] = "";
                fields["amount"] = "";
                fields["payerName"] = "";
                fields["nicNo"] = "";
                fields["contactNo"] = "";
                fields["emailAddress"] = "";
                fields["address"] = "";
                this.setState({fields:fields});
                alert("Form submitted");
            }
      
        }
    
        validateForm =() =>{
    
            let fields = this.state.fields;
            let errors = {};
            let formIsValid = true;
      
            if (!fields["patientName"]) {
              formIsValid = false;
              errors["patientName"] = "*Please enter your username.";
            }
      
            if (typeof fields["patientName"] !== "undefined") {
              if (!fields["patientName"].match(/^[a-zA-Z ]*$/)) {
                formIsValid = false;
                errors["patientName"] = "*Please enter alphabet characters only.";
              }
            }
      
            if (!fields["emailAddress"]) {
              formIsValid = false;
              errors["emailAddress"] = "*Please enter your email-ID.";
            }
      
            if (typeof fields["emailAddress"] !== "undefined") {
              //regular expression for email validation
              var pattern = new RegExp(/^(("[\w-\s]+")|([\w-]+(?:\.[\w-]+)*)|("[\w-\s]+")([\w-]+(?:\.[\w-]+)*))(@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][0-9]\.|1[0-9]{2}\.|[0-9]{1,2}\.))((25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\.){2}(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\]?$)/i);
              if (!pattern.test(fields["emailAddress"])) {
                formIsValid = false;
                errors["emailAddress"] = "*Please enter valid email-ID.";
              }
            }
      
            if (!fields["contactNo"]) {
              formIsValid = false;
              errors["contactNo"] = "*Please enter your mobile no.";
            }
      
            if (typeof fields["contactNo"] !== "undefined") {
              if (!fields["contactNo"].match(/^[0-9]{10}$/)) {
                formIsValid = false;
                errors["contactNo"] = "*Please enter valid mobile no.";
              }
            }
      
            if (!fields["payerName"]) {
              formIsValid = false;
              errors["payerName"] = "*Please enter your password.";
            }
    
            if (!fields["amount"]) {
                formIsValid = false;
                errors["amount"] = "*Please enter your password.";
            }
    
            if (!fields["nicNo"]) {
                formIsValid = false;
                errors["nicNo"] = "*Please enter your password.";
            }
    
            if (!fields["address"]) {
                formIsValid = false;
                errors["address"] = "*Please enter your password.";
            }
      
      
            this.setState({
              errors: errors
            });
            return formIsValid;
      
      
          }
        
    }

    render () {
        return (
            
            <div className="col-md-8 mt-4 mx-auto row">
                <h1 className="h3 mb-3 font-weight-normal" style={{color:'#0d47a1'}}>Payment Information </h1>
                    <form method="post" className="needs-validation"  onSubmit= {this.submituserRegistrationForm}>
                        <div className="form-group" style={{marginBottom:'15px'}}>
                            <label style={{marginBottom: '5px', fontWeight: 'bold', color:'#1976d2'}}>Payment Type</label>
                            <select 
                            className="form-control"
                            name="paymentType"
                            value={this.state.fields.paymentType}
                            onChange={this.handleInputChange}>

                                <option selected>Payment Type</option>
                                <option>Covide Care Charges</option>
                                <option>RT-PCR Test</option>
                                <option>Rapid PCR Tests</option>
                                <option>Consultant fees Receipt</option>
                                <option>Final Bill</option>
                                <option>General Receipt</option>
                                <option>Lab Receipt</option>
                                <option>Pharmacy Bill</option>

                            </select>
                        </div>

                        <div className="form-group" style={{marginBottom:'15px'}}>
                            <label style={{marginBottom: '5px', fontWeight: 'bold', color:'#1976d2'}}>Invoice No</label>
                            <input type="text"
                            className="form-control"
                            name="invoiceNo"
                            placeholder="Enter Invoice No."
                            value={this.state.fields.invoiceNo}
                            onChange={this.handleInputChange}/>
                        </div>

                        <div className="form-group" style={{marginBottom:'15px'}}>
                            <label style={{marginBottom: '5px', fontWeight: 'bold', color:'#1976d2'}}>Patient Name</label>
                            <input type="text"
                            className="form-control"
                            name="patientName"
                            placeholder="Enter the Patient Name"
                            value={this.state.fields.patientName}
                            onChange={this.handleInputChange}/>
                            <div className="errorMsg">{this.state.errors.patientName}</div>
                        </div>

                        <div className="form-group" style={{marginBottom:'15px'}}>
                            <label style={{marginBottom: '5px', fontWeight: 'bold', color:'#1976d2'}}>Amount</label>
                            <input type="text"
                            className="form-control"
                            name="amount"
                            placeholder="Ex:4500.00"
                            value={this.state.fields.amount}
                            onChange={this.handleInputChange}/>
                            <div className="errorMsg">{this.state.errors.amount}</div>
                        </div>
                        <br/>
                    </form>
                
                <h1 className="h3 mb-3 font-weight-normal" style={{color:'#0d47a1'}}>Payer Information</h1>
                    <form method="post" className="needs-validation" noValidate>
                        <div className="form-group" style={{marginBottom:'15px'}}>
                            <label style={{marginBottom: '5px', fontWeight: 'bold', color:'#1976d2'}}>Payer Name</label>
                            <input type="text"
                            className="form-control"
                            name="payerName"
                            placeholder="Enter the Payer Name"
                            value={this.state.fields.payerName}
                            onChange={this.handleInputChange}/>
                            <div className="errorMsg">{this.state.errors.payerName}</div>
                        </div>

                        <div className="form-group" style={{marginBottom:'15px'}}>
                            <label style={{marginBottom: '5px', fontWeight: 'bold', color:'#1976d2'}}>NIC No</label>
                            <input type="text"
                            className="form-control"
                            name="nicNo"
                            placeholder="NIC No."
                            value={this.state.fields.nicNo}
                            onChange={this.handleInputChange}/>
                            <div className="errorMsg">{this.state.errors.nicNo}</div>
                        </div>

                        <div className="form-group" style={{marginBottom:'15px'}}>
                            <label style={{marginBottom: '5px', fontWeight: 'bold', color:'#1976d2'}}>Contac No</label>
                            <input type="text"
                            className="form-control"
                            name="contactNo"
                            placeholder="07xxxxxxxx"
                            value={this.state.fields.contactNo}
                            onChange={this.handleInputChange}/>
                            <div className="errorMsg">{this.state.errors.contactNo}</div>
                        </div>

                        <div className="form-group" style={{marginBottom:'15px'}}>
                            <label style={{marginBottom: '5px', fontWeight: 'bold', color:'#1976d2'}}>Email Address</label>
                            <input type="text"
                            className="form-control"
                            name="emailAddress"
                            placeholder="example@gmail.com"
                            value={this.state.fields.emailAddress}
                            onChange={this.handleInputChange}/>
                            <div className="errorMsg">{this.state.errors.emailAddress}</div>
                        </div>

                        <div className="form-group" style={{marginBottom:'15px'}}>
                            <label style={{marginBottom: '5px', fontWeight: 'bold', color:'#1976d2'}}>Address</label>
                            <input type="text"
                            className="form-control"
                            name="address"
                            placeholder="Enter the Address"
                            value={this.state.fields.address}
                            onChange={this.handleInputChange}/>
                            <div className="errorMsg">{this.state.errors.address}</div>
                        </div>


                        <button className="btn btn-primary" type="submit" style={{marginTop:'15px',marginLeft:'8px'}}  onClick={this.onSubmit}>
                            <i className="far fa-check-square"></i>
                            &nbsp; Submit
                        </button>
                       
                    </form>
            </div>
                          
        );
    }
}
