import React, { Component } from 'react'
import axios from 'axios'

export default class EditPost extends Component {

    constructor(props){
        super(props);
        this.state={
            payerName:"",
            nicNo:"",
            contactNo:"",
            emailAddress:"",
            address:""
        }
    }

    handleInputChange = (e) =>{
        const {name,value} = e.target;

        this.setState({
            ...this.state,
            [name]:value
        })
    }

    onSubmit = (e) =>{
        
        e.preventDefault();
        const id = this.props.match.params.id;

        const {payerName,nicNo,contactNo,emailAddress,address} = this.state;

        const data ={
            payerName:payerName,
            nicNo:nicNo,
            contactNo:contactNo,
            emailAddress:emailAddress,
            address:address
        }

        console.log(data)

        axios.put('/post/update/${id}',data).then((res) =>{
            if(res.data.success){
                alert("Post Update Successfully")
                this.setState(
                    {
                        payerName:"",
                        nicNo:"",
                        contactNo:"",
                        emailAddress:"",
                        address:"" 
                    }
                )
            }
        })
    }

    componentDidMount(){

        const id = this.props.match.params.id;

        axios.get(`/post/${id}`).then((res) =>{
            if(res.data.success){
                this.setState({
                    payerName:res.data.post.payerName,
                    nicNo:res.data.post.payerName,
                    payerName:res.data.post.payerName,
                    payerName:res.data.post.payerName,
                    payerName:res.data.post.payerName,

                });

                console.log(this.state.post);
            }
        });
    }

    render() {
        return (
            <div className="col-md-8 mt-4 mx-auto">
                <h1 className="h3 mb-3 font-weight-normal">Edit Post</h1>
                    <form className="needs-validation" noValidate>
                        <div className="form-group" style={{marginBottom:'15px'}}>
                            <label style={{marginBottom: '5px'}}>Payer Name</label>
                            <input type="text"
                            className="form-control"
                            name="payerName"
                            placeholder="Enter Payer Name"
                            value={this.state.payerName}
                            onChange={this.handleInputChange}/>
                        </div>

                        <div className="form-group" style={{marginBottom:'15px'}}>
                            <label style={{marginBottom: '5px'}}>NIC No</label>
                            <input type="text"
                            className="form-control"
                            name="nicNo"
                            placeholder="Enter NIC No"
                            value={this.state.nicNo}
                            onChange={this.handleInputChange}/>
                        </div>

                        <div className="form-group" style={{marginBottom:'15px'}}>
                            <label style={{marginBottom: '5px'}}>Contac No</label>
                            <input type="text"
                            className="form-control"
                            name="contactNo"
                            placeholder="Enter Contact No"
                            value={this.state.contactNo}
                            onChange={this.handleInputChange}/>
                        </div>

                        <div className="form-group" style={{marginBottom:'15px'}}>
                            <label style={{marginBottom: '5px'}}>Email Address</label>
                            <input type="text"
                            className="form-control"
                            name="emailAddress"
                            placeholder="Enter Email Address"
                            value={this.state.emailAddress}
                            onChange={this.handleInputChange}/>
                        </div>

                        <div className="form-group" style={{marginBottom:'15px'}}>
                            <label style={{marginBottom: '5px'}}>Address</label>
                            <input type="text"
                            className="form-control"
                            name="address"
                            placeholder="Enter Address"
                            value={this.state.address}
                            onChange={this.handleInputChange}/>
                        </div>

                        <button className="btn btn-success" type="submit" style={{marginTop:'15px'}} onClick={this.onSubmit}>
                            <i className="far fa-check-square"></i>
                            &nbsp; Update
                        </button>
                    </form>
               
            </div>
        )
    }
}
