import React, { useEffect, useState } from "react";
import axios from "axios";
import {Link} from 'react-router-dom';
import jsPDF from 'jspdf';
import 'jspdf-autotable';



export default function Report() {
  
          
    const genPDF =  (props) => {
    const doc = new jsPDF();
    doc.setFontSize(20);
    doc.text("Menu items report", 70,10)
  
    doc.autoTable({
      html: '#details'
    })
    doc.save("MenuItemsReport.pdf");
  }
            
        
    const [items, setItems] = useState([]);
    useEffect(() => {
        axios
            .get("http://localhost:8070/item/")
            .then(res => setItems(res.data))
            .catch(error => console.log(error));
    });
    const current = new Date();
    const date = `${current.getDate()}/${current.getMonth()+1}/${current.getFullYear()}`;

    return(
      <div className="menu">
        <div className="chef-container" >
        <div className="container pt-5" >
          <div className="row justify-content-sm-center pt-8">
            <div className="col-sm-6 shadow round pb-5">
                <br/> <br/>
                <div id="details" style={{textAlign:'center'}}>
                  <center>
                   <h3><div className="Title" style={{fontFamily: "courier New"}}><i className="fas fa-crown"></i><b> ROYAL GRAND PARADISE</b></div> </h3>  
                  <h6 style={{textAlign:'center'}}> Kandy , peradeniya road </h6> 
                  <h6 style={{textAlign:'center'}}>  Tel: + (94) 081 2233026</h6>
                  <h6 style={{textAlign:'center'}}> RoyalGrandParadise@hotmail.com</h6>
                  <br/> 
                  <h3 style={{textAlign:'center'}}> <b>Menu Items Report</b></h3>
                  <h6 style={{textAlign:'center'}}> Report generated on: {date} </h6>
                  <br/>
                  </center>
                  </div>
      
        <table className="table table-striped"   >
              
            <thead>
                <tr>
                    <th scope="col">Item Image</th>
                    <th scope="col">Item Name</th>
                    <th scope="col">Price</th>
                    <th scope="col">Description</th>
                </tr>
            </thead>    
            <tbody>
                {items.map((item, key) => (
                <tr key={key}>
                    {/* <td><img src={require("../../Images/Upload/kioskFoodItem/"+ item.img).default} style={{width: "5vw"}}></img></td> */}
                    <td>{item.itemName}</td>
                    <td>{item.price}</td>
                    <td>{item.description}</td>
                </tr>        
                ))}                
            </tbody> 
            
        </table>
        <div style={{textAlign:'center'}}>
        <button  className="btn btn-outline-success" type ="primary">
        <i href="#" onClick={() =>this.genPDF()}>
                Download PDF </i>
            </button>
            &nbsp;&nbsp;&nbsp;&nbsp;
        </div>
        </div>
        </div>
        </div>
        </div>
        </div>
    
        
    );

}
