import React, { Component } from 'react'
import axios from 'axios';


export default class CreatePayment extends Component {

    constructor(props){
        super(props);
        
        this.state={

            
            paymentType:"",
            invoiceNo:"",
            patientName:"",
            amount:"",
            payerName:"",
            nicNo:"",
            contactNo:"",
            emailAddress:"",
            address:"",
            
            person: this.props.person,
            errors : {}
        }

 
    }

    handleInputChange = (e) =>{
        const {name,value} = e.target;

        this.setState({
            ...this.state,
            [name]:value
        })
    }


    onSubmit = (e) =>{
        e.preventDefault();

        let errors = this.validateAll();
        if(this.isValid(errors))
            this.props.onSubmit(this.state.person);
        else{
            let s1 = {...this.state};
            s1.errors = errors;
            this.setState(s1);
        }

        const {paymentType,invoiceNo,patientName,amount,payerName,nicNo,contactNo,emailAddress,address} = this.state;

        const data ={
            paymentType:paymentType,
            invoiceNo:invoiceNo,
            patientName:patientName,
            amount:amount,
            payerName:payerName,
            nicNo:nicNo,
            contactNo:contactNo,
            emailAddress:emailAddress,
            address:address
        }
        console.log(data)

        axios.post("/post/save",data).then((res) =>{
            if(res.data.success){
                this.setState(
                    {
                        paymentType:"",
                        invoiceNo:"",
                        patientName:"",
                        amount:"",
                        payerName:"",
                        nicNo:"",
                        contactNo:"",
                        emailAddress:"",
                        address:""
                    }

                    
                )
            }
        })
        
        this.props.history.push('/confirm')
        
    }
    isValid = (errors) =>{
        let keys = Object.keys(errors);
        let count = keys.reduce((acc,curr) => errors[curr] ? acc+1 : acc)
        return count ===0;
    }

    validateAll = () =>{
        let {patientName,amount,payerName,nicNo,contactNo,emailAddress,address} = this.state.person;
        let errors = {};
        if (!patientName) errors.patientName = "You must enter name";
        if (!amount) errors.amount = "You must enter amount";
        if (!payerName) errors.payerName = "You must enter name";
        if (!nicNo) errors.nicNo = "You must enter nic no";
        if (!contactNo) errors.contactNo = "You must enter contact no";
        if (!emailAddress) errors.emailAddress = "You must enter email";
        if (!address) errors.address = "You must enter address";
        return errors;
    };

    handlesubmit = (e)
    render() {

        let {patientName,amount,payerName,nicNo,contactNo,emailAddress,address} = this.state.person;
        let {countries, errors} = this.state;

        return (
            
            <div className="col-md-8 mt-4 mx-auto row">
                <h1 className="h3 mb-3 font-weight-normal" style={{color:'#0d47a1'}}>Payment Information </h1>
                    <form className="needs-validation" noValidate>
                        <div className="form-group" style={{marginBottom:'15px'}}>
                            <label style={{marginBottom: '5px', fontWeight: 'bold', color:'#1976d2'}}>Payment Type</label>
                            <select 
                            className="form-control"
                            name="paymentType"
                            value={this.state.paymentType}
                            onChange={this.handleInputChange}>

                                <option selected>Payment Type</option>
                                <option>Covide Care Charges</option>
                                <option>RT-PCR Test</option>
                                <option>Rapid PCR Tests</option>
                                <option>Consultant fees Receipt</option>
                                <option>Final Bill</option>
                                <option>General Receipt</option>
                                <option>Lab Receipt</option>
                                <option>Pharmacy Bill</option>

                            </select>
                        </div>

                        <div className="form-group" style={{marginBottom:'15px'}}>
                            <label style={{marginBottom: '5px', fontWeight: 'bold', color:'#1976d2'}}>Invoice No</label>
                            <input type="text"
                            className="form-control"
                            name="invoiceNo"
                            placeholder="Enter Invoice No."
                            value={this.state.invoiceNo}
                            onChange={this.handleInputChange}/>
                        </div>

                        <div className="form-group" style={{marginBottom:'15px'}}>
                            <label style={{marginBottom: '5px', fontWeight: 'bold', color:'#1976d2'}}>Patient Name</label>
                            <input type="text"
                            className="form-control"
                            name="patientName"
                            placeholder="Enter the Patient Name"
                            value={this.state.patientName}
                            onChange={this.handleInputChange}
                            />
                            {errors.patientName ? ( <span className="text-danger">{errors.patientName}</span>
                            ) : (
                                ""
                            )}
                        </div>

                        <div className="form-group" style={{marginBottom:'15px'}}>
                            <label style={{marginBottom: '5px', fontWeight: 'bold', color:'#1976d2'}}>Amount</label>
                            <input type="text"
                            className="form-control"
                            name="amount"
                            placeholder="Ex:4500.00"
                            value={this.state.amount}
                            onChange={this.handleInputChange}/>
                        </div>
                        <br/>
                    </form>
                
                <h1 className="h3 mb-3 font-weight-normal" style={{color:'#0d47a1'}}>Payer Information</h1>
                    <form className="needs-validation" noValidate>
                        <div className="form-group" style={{marginBottom:'15px'}}>
                            <label style={{marginBottom: '5px', fontWeight: 'bold', color:'#1976d2'}}>Payer Name</label>
                            <input type="text"
                            className="form-control"
                            name="payerName"
                            placeholder="Enter the Payer Name"
                            value={this.state.payerName}
                            onChange={this.handleInputChange}/>
                        </div>

                        <div className="form-group" style={{marginBottom:'15px'}}>
                            <label style={{marginBottom: '5px', fontWeight: 'bold', color:'#1976d2'}}>NIC No</label>
                            <input type="text"
                            className="form-control"
                            name="nicNo"
                            placeholder="NIC No."
                            value={this.state.nicNo}
                            onChange={this.handleInputChange}/>
                        </div>

                        <div className="form-group" style={{marginBottom:'15px'}}>
                            <label style={{marginBottom: '5px', fontWeight: 'bold', color:'#1976d2'}}>Contac No</label>
                            <input type="text"
                            className="form-control"
                            name="contactNo"
                            placeholder="07xxxxxxxx"
                            value={this.state.contactNo}
                            onChange={this.handleInputChange}/>
                        </div>

                        <div className="form-group" style={{marginBottom:'15px'}}>
                            <label style={{marginBottom: '5px', fontWeight: 'bold', color:'#1976d2'}}>Email Address</label>
                            <input type="text"
                            className="form-control"
                            name="emailAddress"
                            placeholder="example@gmail.com"
                            value={this.state.emailAddress}
                            onChange={this.handleInputChange}/>
                        </div>

                        <div className="form-group" style={{marginBottom:'15px'}}>
                            <label style={{marginBottom: '5px', fontWeight: 'bold', color:'#1976d2'}}>Address</label>
                            <input type="text"
                            className="form-control"
                            name="address"
                            placeholder="Enter the Address"
                            value={this.state.address}
                            onChange={this.handleInputChange}/>
                        </div>


                        <button className="btn btn-primary" type="submit" style={{marginTop:'15px',marginLeft:'8px'}}  onClick={this.onSubmit}>
                            <i className="far fa-check-square"></i>
                            &nbsp; Submit
                        </button>
                       
                    </form>
            </div>
                          
        );
    }
}
