import React, { Component } from 'react'
import axios from 'axios';

export default class RefundSearch extends Component {

    constructor(props){
        super(props);
        this.state={
            referenceNo:{
                value:"",
                valid: true
            },
            nicreferenceNo:{
                value:"",
                valid: true
            }
            
           
        }
    }

    componentDidMount(){
        this.retrievePosts();
      
    }
      
    retrievePosts(){
        axios.get("/searchPosts").then(res =>{
          if(res.data.success){
            this.setState({
              posts:res.data.existingPosts
            });
      
            console.log(this.state.posts)
          }
      
      
        });
    }

    submitHandler = event => {
        event.preventDefault();
        event.target.className += " was-validated";
      };
    
      changeHandler = event => {
        this.setState({ [event.target.name]: event.target.value});
      };

    render() {
        return (
            <div className="col-md-8 mt-4 mx-auto row">
                <h1 className="h3 mb-3 font-weight-normal " style={{color:'#0d47a1'}}></h1>
                    <form className="needs-validation w-75 border border-info" noValidate style={{paddingBottom:'30px',paddingTop:'30px', marginTop:'180px'}}>
                       

                        <div className="form-group" style={{marginBottom:'15px'}}>
                            <label style={{marginBottom: '5px', fontWeight: 'bold', color:'#1976d2'}}>Reference No.</label>
                            <input type="text"
                            className="form-control"
                            value={this.state.referenceNo}
                            onChange={this.changeHandler}
                            name="referenceNo"
                            required
                            placeholder="Enter Reference No."/>
                        </div>

                        <div className="form-group" style={{marginBottom:'15px'}}>
                            <label style={{marginBottom: '5px', fontWeight: 'bold', color:'#1976d2'}}>NIC No.</label>
                            <input type="text"
                            className="form-control"
                            value={this.state.nicno}
                            onChange={this.changeHandler}
                            name="nicno"
                            required
                            placeholder="Enter NIC No."/>
                        </div>

                        <button className="btn btn-primary " type="submit" style={{marginTop:'10px', marginLeft:'8px'}}>
                            <a href={`/refundDetails`} style={{textDecoration:'none',color:'white'}}>
                                Search
                            </a>
                        </button>
                        <br/>
                     
                    </form>
                          
            </div>
        )
    }
}
