import React, { Component } from 'react'
import axios from 'axios';


export default class CreatePayment extends Component {

    constructor(props){
        super(props);
        this.state={
            user:{
                paymentType:"",
                invoiceNo:"",
                patientName:"",
                amount:"",
                payerName:"",
                nicNo:"",
                contactNo:"",
                emailAddress:"",
                address:"",
            },
            
            invalidNumber: false,
            invalidEmail: false,
            submitted: false,
            invalidFields: []
        }

    };

    validateNumber = (value, name) => {
        const regex = /^[0-9]\d*$/;
        if (value && !regex.test(value)) {
          this.setState({
            invalidNumber: true,
            invalidFields: [...this.state.invalidFields, name]
          });
        } else {
          this.setState({
            invalidNumber: false,
            invalidFields: this.state.invalidFields.filter(item => item !== name)
          });
        }
    };

    validateEmail = (value, name) => {
        const regex = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;
        if (value && !regex.test(value)) {
          this.setState({
            invalidEmail: true,
            invalidFields: [...this.state.invalidFields, name]
          });
        } else {
          this.setState({
            invalidEmail: false,
            invalidFields: this.state.invalidFields.filter(item => item !== name)
          });
        }
    };



    handleInputChange = (e) =>{
        const {name,value} = e.target;
        const { user } = this.state;

        this.setState({
            user:{
                ...user,
                [name]:value
            }
        });

        if (name === "emailAddress") {
            this.validateEmail(value, name);
          }
          if (name === "invoiceNo") {
            this.validateNumber(value, name);
          }
          if (name === "contactNo") {
            this.validateNumber(value, name);
          }
          if (name === "amount") {
            this.validateNumber(value, name);
          }
    };

    handleSubmit = e => {
        e.preventDefault();
    
        const { patientName, amount, payerName, nicNo, contactNo, emailAddress,address } = this.state.user;
        this.setState({
          submitted: true
        });
    
        if (
          patientName !== "" &&
          amount !== "" &&
          payerName !== "" &&
          nicNo !== "" &&
          contactNo !== "" &&
          emailAddress !== "" &&
          address !== ""
        ) {
          console.log("User created : ", this.state.user);
        } else {
          console.log("Error creating user");
        }
      };

    onSubmit = (e) =>{
        e.preventDefault();

        const {paymentType,invoiceNo,patientName,amount,payerName,nicNo,contactNo,emailAddress,address} = this.state;

        const data ={
            paymentType:paymentType,
            invoiceNo:invoiceNo,
            patientName:patientName,
            amount:amount,
            payerName:payerName,
            nicNo:nicNo,
            contactNo:contactNo,
            emailAddress:emailAddress,
            address:address
        }
        console.log(data)

        axios.post("/post/save",data).then((res) =>{
            if(res.data.success){
                this.setState(
                    {
                        paymentType:"",
                        invoiceNo:"",
                        patientName:"",
                        amount:"",
                        payerName:"",
                        nicNo:"",
                        contactNo:"",
                        emailAddress:"",
                        address:""
                    }

                    
                )
            }
        })
        
        this.props.history.push('/confirm')
        
    }

    render() {

        const { submitted, invalidEmail, user } = this.state;

        return (
            
            <div className="col-md-8 mt-4 mx-auto row">
                <h1 className="h3 mb-3 font-weight-normal" style={{color:'#0d47a1'}}>Payment Information </h1>
                    <form onSubmit={this.handleSubmit}>
                        <div className="form-group" style={{marginBottom:'15px'}}>
                            <label style={{marginBottom: '5px', fontWeight: 'bold', color:'#1976d2'}}>Payment Type</label>
                            <select 
                            className="form-control"
                            name="paymentType"
                            value={this.state.user.paymentType}
                            onChange={this.handleInputChange}>

                                <option selected>Payment Type</option>
                                <option>Covide Care Charges</option>
                                <option>RT-PCR Test</option>
                                <option>Rapid PCR Tests</option>
                                <option>Consultant fees Receipt</option>
                                <option>Final Bill</option>
                                <option>General Receipt</option>
                                <option>Lab Receipt</option>
                                <option>Pharmacy Bill</option>

                            </select>
                        </div>

                        <div className="form-group" style={{marginBottom:'15px'}}>
                            <label style={{marginBottom: '5px', fontWeight: 'bold', color:'#1976d2'}}>Invoice No</label>
                            <input type="text"
                            className="form-control"
                            name="invoiceNo"
                            placeholder="Enter Invoice No."
                            value={this.state.user.invoiceNo}
                            onChange={this.handleInputChange}/>
                        </div>

                        <div className="form-group" style={{marginBottom:'15px'}}>
                            <label style={{marginBottom: '5px', fontWeight: 'bold', color:'#1976d2'}}>Patient Name</label>
                            <input type="text"
                            className="form-control"
                            name="patientName"
                            placeholder="Enter the Patient Name"
                            value={this.state.user.patientName}
                            onChange={this.handleInputChange}/>

                            {submitted && !user.pa && (
                                <div style={{ color: "red" }}>Name is required</div>
                            )}
                        </div>

                        <div className="form-group" style={{marginBottom:'15px'}}>
                            <label style={{marginBottom: '5px', fontWeight: 'bold', color:'#1976d2'}}>Amount</label>
                            <input type="text"
                            className="form-control"
                            name="amount"
                            placeholder="Ex:4500.00"
                            value={this.state.user.amount}
                            onChange={this.handleInputChange}/>
                        </div>
                        <br/>
                    </form>
                
                <h1 className="h3 mb-3 font-weight-normal" style={{color:'#0d47a1'}}>Payer Information</h1>
                    <form onSubmit={this.handleSubmit}>
                        <div className="form-group" style={{marginBottom:'15px'}}>
                            <label style={{marginBottom: '5px', fontWeight: 'bold', color:'#1976d2'}}>Payer Name</label>
                            <input type="text"
                            className="form-control"
                            name="payerName"
                            placeholder="Enter the Payer Name"
                            value={this.state.user.payerName}
                            onChange={this.handleInputChange}/>
                        </div>

                        <div className="form-group" style={{marginBottom:'15px'}}>
                            <label style={{marginBottom: '5px', fontWeight: 'bold', color:'#1976d2'}}>NIC No</label>
                            <input type="text"
                            className="form-control"
                            name="nicNo"
                            maxLength={15}
                            placeholder="NIC No."
                            value={this.state.user.nicNo}
                            onChange={this.handleInputChange}/>
                        </div>

                        <div className="form-group" style={{marginBottom:'15px'}}>
                            <label style={{marginBottom: '5px', fontWeight: 'bold', color:'#1976d2'}}>Contac No</label>
                            <input type="text"
                            className="form-control"
                            name="contactNo"
                            placeholder="07xxxxxxxx"
                            maxLength={10}
                            value={this.state.user.contactNo}
                            onChange={this.handleInputChange}/>
                        </div>

                        <div className="form-group" style={{marginBottom:'15px'}}>
                            <label style={{marginBottom: '5px', fontWeight: 'bold', color:'#1976d2'}}>Email Address</label>
                            <input type="text"
                            className="form-control"
                            name="emailAddress"
                            placeholder="example@gmail.com"
                            value={this.state.user.emailAddress}
                            onChange={this.handleInputChange}/>
                        </div>

                        <div className="form-group" style={{marginBottom:'15px'}}>
                            <label style={{marginBottom: '5px', fontWeight: 'bold', color:'#1976d2'}}>Address</label>
                            <input type="text"
                            className="form-control"
                            name="address"
                            placeholder="Enter the Address"
                            value={this.state.user.address}
                            onChange={this.handleInputChange}/>
                        </div>


                        <button className="btn btn-primary" type="submit" style={{marginTop:'15px',marginLeft:'8px'}}  onClick={this.onSubmit}>
                            <i className="far fa-check-square"></i>
                            &nbsp; Submit
                        </button>
                       
                    </form>
            </div>
                          
        );
    }
}
