import React, { Component } from 'react';
import axios from "axios";

export default class test extends Component {
    constructor(props) {
        super(props);
        this.state = {
            paymentType:"",
            invoiceNo:"",
            patientName:"",
            amount:"",
            payerName:"",
            nicNo:"",
            contactNo:"",
            emailAddress:"",
            address:"",
        };
        this.state = { user: [] };
    }

    handleChange = (e) => {
        this.setState({ [e.target.name]: e.target.value });
    };

    handleSubmit = (e) => {
        e.preventDefault();

        
        const data = {
            paymentType: this.state.paymentType,
            invoiceNo: this.invoiceNo,
            patientName: this.patientName,
            amount: this.amount,
            payerName: this.payerName,
            nicNo: this.state.nicNo,
            contactNo: this.state.contactNo,
            emailAddress: this.state.emailAddress,
            address: this.state.address,
        }
        console.log(data);

        axios.post("/post/save",data).then((res) =>{
            if(res.data.success){
                this.setState(
                    {
                        paymentType:"",
                        invoiceNo:"",
                        patientName:"",
                        amount:"",
                        payerName:"",
                        nicNo:"",
                        contactNo:"",
                        emailAddress:"",
                        address:""
                    }

                    
                )
            }
        })
        
        this.props.history.push('/confirm');

    };

    reset() {
        const res = {
            paymentType:"",
            invoiceNo:"",
            patientName:"",
            amount:"",
            payerName:"",
            nicNo:"",
            contactNo:"",
            emailAddress:"",
            address:"",
        }
    }

    // componentDidMount() {
    //     axios
    //         .get("http://localhost:4000/user/")
    //         .then((response) => {
    //             this.setState({ user: response.data });
    //         })
    //         .catch(function (error) {
    //             console.log(error);
    //         })
    // }
    render() {
        return (
            <div>
                <div className="Container">
                    <form onSubmit={this.handleSubmit}>
                        <input type="hidden" name="_id" />
                        <div className="form-group">
                            <div>
                                <label>Payment Type</label>
                                <select className="form-control" name="paymentType" placeholder="Pyement type" required value={this.state.paymentType} onChange={this.handleChange}>
                                    <option>Choose Title</option>
                                    <option>Rev</option>
                                    <option>Mr</option>
                                    <option>Ms</option>
                                    <option>Mrs</option>
                                    <option>Master</option>
                                </select>
                            </div>
                        </div>
                        <div className="form-group">
                            <label className="col-sm-2 col-form-label">Invoice No</label>
                            <div>
                                <input type="text" className="form-control" name="invoiceNo" placeholder="Enter Name" required value={this.state.invoiceNo} onChange={this.handleChange}/>
                            </div>
                        </div>
                        <div className="form-group">
                            <label className="col-sm-3 col-form-label">Patient Name</label>
                            <div>
                                <input type="text" className="form-control" name="patientName" placeholder="Enter Name" required value={this.state.patientName} onChange={this.handleChange}/>
                            </div>
                        </div>
                        <div className="form-group">
                            <label className="col-sm-2 col-form-label">Amount</label>
                            <div>
                                <input type="text" className="form-control" name="amount" placeholder="Enter Name" required value={this.state.amount} onChange={this.handleChange}/>
                            </div>
                        </div>
                        <div className="form-group">
                            <label className="col-sm-2 col-form-label">Payer Name</label>
                            <div>
                                <input type="text" className="form-control" name="payerName" placeholder="Enter Name" required value={this.state.payerName} onChange={this.handleChange}/>
                            </div>
                        </div>
                        <div className="form-group">
                            <label className="col-sm-2 col-form-label">NIC No</label>
                            <div>
                                <input type="text" className="form-control" name="nicNo" placeholder="Enter NIC" pattern="[0-9]{3}[0-9]{3}[0-9]{3}{1}[v/V] || [0-9]{4}[0-9]{4}[0-9]{4}" value={this.state.nicNo} onChange={this.handleChange}/>
                            </div>
                        </div>
                        <div className="form-group">
                            <label className="col-sm-2 col-form-label">Contact No</label>
                            <div>
                                <input type="text" className="form-controlt" name="contactNo" placeholder="Enter Phone No" pattern="[0-9]{3}[0-9]{3}[0-9]{4}" value={this.state.contactNo} onChange={this.handleChange}/>
                            </div>
                        </div>
                        <div className="form-group">
                            <label className="col-sm-3 col-form-label">Email Address</label>
                            <div>
                                <input type="email" className="form-control" name="emailAddress" placeholder="Enter Email" required value={this.state.emailAddress} onChange={this.handleChange}/>
                            </div>
                        </div>
                        <div className="form-group">
                            <label className="col-sm-2 col-form-label">Address</label>
                            <div>
                                <input type="text" className="form-control" name="address" placeholder="Enter Phone No"  value={this.state.address} onChange={this.handleChange}/>
                            </div>
                        </div>
                        <div className="form-group">
                            <div>
                            <button type="submit" className="btn btn-primary">Submit</button>
                            </div>
                        </div>
                    </form>
                </div>
            </div>
        )
    }
}
